    private final static Pattern emailAddressValidationRegexPattern_ = Pattern.compile(emailAddressValidationRegexString_);
    
    public static boolean isValidEmailAddress(String emailAddress) {
        
        if ((emailAddress == null) || emailAddress.isEmpty()) {
            return false;
        }
        
        try {
            Matcher matcher = emailAddressValidationRegexPattern_.matcher(emailAddress);
            boolean isEmailAddressValid = matcher.matches();
            
            return isEmailAddressValid;
        }
        catch (Exception e) {
            logger.error(e.toString() + System.lineSeparator() + StackTrace.getStringFromStackTrace(e));
            return false;
        }
        
    }
    
    public static List getValidEmailAddresses(List<String> emailAddresses) {
        
        if ((emailAddresses == null) || emailAddresses.isEmpty()) {
            return new ArrayList<>();
        }
        
        ArrayList emailAddessesList = new ArrayList<>();

        for (String emailAddress : emailAddresses) {
            String trimmedEmailAddress = emailAddress.trim();
            boolean isValidEmailAddress = EmailUtils.isValidEmailAddress(trimmedEmailAddress);

            if (isValidEmailAddress) {
                emailAddessesList.add(trimmedEmailAddress);
            }
        }
        
        return emailAddessesList;
    }
    
